export const initialState = {
	user: null,
	playlist: [],
	playing: false,
	item: null,
};

export const reducer = (state, action) => {
	console.log(action);

  //Action -> type, [payload]
  switch(action.type){
    case 'SET_USER':
      return {
        ...state
      }
  }
};
