// https://developer.spotify.com/documentation/web-playback-sdk/quick-start/#
export const authEndpoint = "https://accounts.spotify.com/authorize";

const redirectUri = "http://localhost:3000/";
const clientId = "159bd40f25e4455ab3e65e5bfce30c44";

const scopes = [
	"user-read-currently-playing",
	"user-read-recently-played",
	"user-read-playback-state",
	"user-top-read",
	"user-modify-playback-state",
];

export const getTokenFromUrl = () => {
	//http://localhost:3000/#access_token=BQCyCMPmE1AvMJv5oiUodjoTmpAIEgbZxGJp-ddXQVBfu7v9Hf6eIHkxxvuOVWiQCuA0vy_X6yGYzM5OXhXSVqX_GdaBlKxzYyf87LjD2Kzwng7KOFrhjJ5aMPqNj_rYxBzxYpLvgAnPFevbcGOq1nqpTIFFswiQC8FrsNpp8a-_Tc6vHVr2&token_type=Bearer&expires_in=3600
	//#access_token=mysupersecretkey&name=shubham
	return window.location.hash
		.substring(1)
		.split("&")
		.reduce((initial, item) => {
			//#access_token=mysupersecretkey&name=shubham
			let parts = item.split("=");
			initial[parts[0]] = decodeURIComponent(parts);
		});
};

export const loginUrl = `${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join(
	"%20"
)}&response_type=token&show_dialog=true`;
